  function generateRecipeList() {
    const recipeListElement = document.getElementById("recipe-list");
    recipes.forEach((recipe, index) => {
      const row = document.createElement("tr");
      row.innerHTML = `
        <td><a href="recipe.html?index=${index}">${recipe.name}</a></td>
        <td>${recipe.category}</td>
        <td>${recipe.description}</td>
      `;
      recipeListElement.appendChild(row);
    });
  }
  
  document.addEventListener("DOMContentLoaded", function() {
    console.log("DOM completamente cargado.");
    generateRecipeList(); 
  });
  
  
function showRecipeDetails() {
    const params = new URLSearchParams(window.location.search);
    const recipeIndex = parseInt(params.get("index"));
    const recipe = recipes[recipeIndex];
    const recipeDetailsElement = document.getElementById("recipe-details");
    const ingredientsList = recipe.ingredients.map(ingredient => `<li>${ingredient}</li>`).join("");
    const instructionsList = recipe.instructions.map(instruction => `<li>${instruction}</li>`).join("");
    recipeDetailsElement.innerHTML = `
      <h2>${recipe.name}</h2>
      <img src="${recipe.image}" alt="${recipe.name}">
      <h3>Ingredientes:</h3>
      <ul>${ingredientsList}</ul>
      <h3>Instrucciones:</h3>
      <ol>${instructionsList}</ol>
    `;
  }
  
  document.addEventListener("DOMContentLoaded", function() {
    console.log("DOM completamente cargado.");
    showRecipeDetails();
});
  